import React from 'react';
import {Avatar, Button, CssBaseline, TextField, FormControlLabel,
        Checkbox, Paper, Box, Grid, Typography,  CircularProgress} from '@material-ui/core';
import Alert from '@material-ui/lab/Alert';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import {useNavigate} from "react-router-dom";
import {useStyles} from "./LoginPageStyles";
import CopyRight from "../../components/ui/CopyRight";
import { Link } from "react-router-dom";
import { checkEmailValidity } from "../../models/User";
import {loginByEmailAndPassword} from "../../components/database/FirebaseAPI";
import {AlertTitle} from "@material-ui/lab";


class LoginForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {email: "", password: "",
                      emailError: "", passwordError: "",
                      firebaseLoginError: "",
                      isLoading: false};
    }

    /**
     * Checks whether credentials entered are valid and set the error messages accordingly
     * @param e the event object triggered by the sign-in button
     */
    validateSignIn = (e) => {

        e.preventDefault();

        if (!checkEmailValidity(this.state.email)) {
            // If email is empty or invalid, set the necessary error for display
            this.setState({emailError: "Please enter a valid email!"});

            // If password is also empty, do the same
            if (!this.state.password) {
                this.setState({passwordError: "Please enter your password!"});
            }

            return;

        }

        // If email and password has been entered, waits for Firebase to give us feed back
        // and set loading state to true to display loading spinner, since the loading spinner
        // only displays if the isLoading state is set to true (see html below).
        this.setState({isLoading: true})

        // Call the encapsulated login method that uses Firebase's signInWithEmailAndPassword method
        loginByEmailAndPassword(this.state.email, this.state.password).then((userCredential) => {
            this.setState({isLoading: false})
            // go to the home page since sign in was successful
            this.props.login();
        }).catch((error) => {
            this.setState({isLoading: false});
            // display the error message returned by Firebase since sign in was not successful
            this.setState({firebaseLoginError: error.message})
        })

    }

    /**
     * Method for displaying Firebase error message
     * @returns {JSX.Element|string}
     */
    displayFirebaseLoginError = () => {

        // If the firebase login error message has been set, display it with an Alert
        if (this.state.firebaseLoginError) {
            return (
                <Alert severity="error">
                    <AlertTitle>Failed to login</AlertTitle>
                    <strong>{this.state.firebaseLoginError}</strong>
                </Alert>
            )
        }

        return ("");
    }


    render() {
        return (
            <div>
                {
                    // Displays loading spinner if we are waiting for Firebase to log user in or giving user an error
                    this.state.isLoading?
                    <CircularProgress color="secondary" className={this.props.classes.loading}/>

                    // Else, just display the login page
                    :
                    <Grid container component="main" className={this.props.classes.root}>
                        <CssBaseline />
                        <Grid item xs={false} sm={4} md={7} className={this.props.classes.image} />
                        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                            <div className={this.props.classes.paper}>
                                <Avatar className={this.props.classes.avatar}>
                                    <LockOutlinedIcon  />
                                </Avatar>
                                <Typography component="h1" variant="h5">
                                    Sign in
                                </Typography>
                                <form className={this.props.classes.form} noValidate onSubmit={this.validateSignIn}>
                                    {this.displayFirebaseLoginError()}
                                    <TextField
                                        variant="outlined"
                                        margin="normal"
                                        required
                                        fullWidth
                                        id="email"
                                        label="Email Address"
                                        name="email"
                                        autoComplete="email"
                                        autoFocus
                                        onChange={(e) =>
                                        {this.setState({email: e.target.value, emailError: ""})}}
                                    />
                                    {this.state.emailError ? <Alert severity="error">{this.state.emailError}</Alert> : ""}
                                    <TextField
                                        variant="outlined"
                                        margin="normal"
                                        required
                                        fullWidth
                                        name="password"
                                        label="Password"
                                        type="password"
                                        id="password"
                                        autoComplete="current-password"
                                        onChange={(e) =>
                                        {this.setState({password: e.target.value, passwordError: ""});}}
                                    />
                                    {this.state.passwordError ? <Alert severity="error">{this.state.passwordError}</Alert> : ""}
                                    <FormControlLabel
                                        control={<Checkbox value="remember" color="primary" />}
                                        label="Remember me"
                                    />
                                    <Button
                                        type="submit"
                                        fullWidth
                                        variant="contained"
                                        color="primary"
                                        className={this.props.classes.submit}
                                    >
                                        Sign In
                                    </Button>
                                    <Grid container>
                                        <Grid item xs>
                                            {/*<Link>*/}
                                            {/*    Forgot password?*/}
                                            {/*</Link>*/}
                                        </Grid>
                                        <Grid item>
                                            <Link className={this.props.classes.link} to="/signup">
                                                {"Don't have an account? Sign up!"}
                                            </Link>
                                        </Grid>
                                    </Grid>
                                    <Box mt={5}>
                                        <CopyRight />
                                    </Box>
                                </form>
                            </div>
                        </Grid>
                    </Grid>
                }
            </div>

        );
    }
}
const LoginPage = () => {
    const classes = useStyles();
    const navigate = useNavigate();

    const login = () => {

        // Navigate to student view by default for now
        // Will need to implement distinctions between logging in as an instructor vs. as a student later
        navigate("/studentView");
    }

    return (
        <LoginForm classes={classes} login={login}/>
    )
}

export default LoginPage;
